mapper.select:
SELECT
member_no as memberNo
from member where member_id = #{memberId}

SELECT
member_pw as memberPw
from member where member_id = #{memberId} and member_pw = #{memberPw}

mapper.update:
update member set member_name = #{memberName}

	,member_depart_name = null
	,member_depart_name = #{memberDepartName}


	,member_main_task = null
	,member_main_task = #{memberMainTask}


	,member_picture = null
	,member_picture = #{memberPicture}

where member_no = #{memberNo}
