mapper.select:
select p.post_no as postNo, p.post_title as postTitle, p.post_progress as postProgress, p.post_date as postDate, p.post_content as postContent, p.post_writer as postWriter, p.post_temp as postTemp, p.post_pro_no as postProNo, p.post_tag as postTag, m.member_name as memberName, m.member_picture as memberPicture from post p, member m where p.post_writer = m.member_no
and p.post_writer = #{memberNo}
and p.post_pro_no = #{proNo} and (p.post_title like '%'||#{keyword}||'%'
or m.member_name like '%'||#{keyword}||'%'
) order by p.post_no desc

select p.post_no as postNo, p.post_title as postTitle, p.post_progress as postProgress, p.post_date as postDate, p.post_content as postContent, p.post_writer as postWriter, p.post_temp as postTemp, p.post_pro_no as postProNo, p.post_tag as postTag, m.member_name as memberName, m.member_picture as memberPicture from post p, member m where p.post_writer = m.member_no and p.post_pro_no = #{proNo}
p.post_tag like '%'||#{searchHelper.hashTags}||'%'
order by p.post_no desc
